document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll("tbody tr").forEach(n=>{n.addEventListener("mouseover",function(){var t=n.querySelector("td").textContent.trim();let e="";n.classList.contains("file")?e="Download file":n.classList.contains("dir")&&(e="Navigate to folder..."),document.querySelector(".command-line").textContent=`- ${t} | `+e})})});